create database PaginaWebBD;
use PaginaWebBD;
create table juegos(
	idJuego int primary key auto_increment not null,
    nombreJuego varchar(50) not null,
    categoriaJuego varchar(30),
    descripcionJuego varchar(10000)  not null,
    ratingJuego varchar(10) not null,
    linkJuego varchar(1500) not null
);
select * from juegos;








